mapper.select:

select

  distinct ${@com.alibaba.security.SecurityUtil@trimSql4OrderBy(distinctSql)}




from opscloud_meta_defender_rule




  order by ${@com.alibaba.security.SecurityUtil@trimSql4OrderBy(orderByClause)}


  limit #{pageIndex},#{pageSize}

select

from opscloud_meta_defender_rule
where 1=1

  and defense_range_type = #{record.defenseRangeType}


  and defense_range_key = #{record.defenseRangeKey}
